mapper.sql:
SELECT A.QANO, A.PROJECTID, A.QATitle, DATE_FORMAT(a.QUESTIONDATE, '%Y/%m/%d') AS QUESTIONDATE, DATE_FORMAT(a.EXPDATE, '%Y/%m/%d') AS EXPDATE, DATE_FORMAT(a.ANSWERDATE, '%Y/%m/%d') AS ANSWERDATE, B.PROJECTNAME, C.GROUPNAME, D.MODULENAME, E. NAME QAASTATE, F. NAME STAGE, G.USERNAME QUESTIONER, H.USERNAME ANSWERER, I. NAME QAQSTATE, J. NAME QUESTIONDIV, K. NAME CAUSEDIV FROM tbl_QA A LEFT JOIN tbl_ProjectMaster B ON A.PROJECTID = B.PROJECTID LEFT JOIN tbl_GroupMaster C ON A.PROJECTID = C.PROJECTID AND A.GROUPID = C.GROUPID LEFT JOIN tbl_ModuleMaster D ON A.PROJECTID = D.PROJECTID AND A.GROUPID = D.GROUPID AND A.MODULEID = D.MODULEID LEFT JOIN tbl_GeneralMaster E ON A.QAASTATE = E. CODE AND E.KBN = '07' LEFT JOIN tbl_GeneralMaster F ON A.STAGE = F. CODE AND F.KBN = '01' LEFT JOIN tbl_UserMaster G ON A.QUESTIONER = G.USERID LEFT JOIN tbl_UserMaster H ON A.ANSWERER = H.USERID LEFT JOIN tbl_GeneralMaster I ON A.QAQSTATE = I. CODE AND I.KBN = '06' LEFT JOIN tbl_GeneralMaster J ON A.QUESTIONDIV = J. CODE AND J.KBN = 'QD' LEFT JOIN tbl_GeneralMaster K ON A.CAUSEDIV = K. CODE AND K.KBN = 'RQ'
AND A.PROJECTID= #{projectId,jdbcType=VARCHAR}
AND A.QANO LIKE CONCAT('%', #{qaNo,jdbcType=VARCHAR}, '%')
AND A.GROUPID = #{groupId,jdbcType=VARCHAR}
AND A.QAASTATE = #{qaAState,jdbcType=VARCHAR}
AND A.QAQState <> '5'
AND A.QAQSTATE = #{qaQState,jdbcType=VARCHAR}
AND A.STAGE = #{stage,jdbcType=VARCHAR}
AND A.QUESTIONDIV = #{questionDiv,jdbcType=VARCHAR}
AND A.CAUSEDIV = #{causeDiv,jdbcType=VARCHAR}
AND A.MODULEID = #{moduleId,jdbcType=VARCHAR}
AND (A.QATitle LIKE CONCAT('%',#{questionInfo,jdbcType=VARCHAR}, '%') OR A.QUESTIONINFO LIKE CONCAT('%',#{questionInfo,jdbcType=VARCHAR}, '%'))
AND (A.QUESTIONER LIKE CONCAT('%',#{questioner,jdbcType=VARCHAR} , '%') OR G.USERNAME LIKE CONCAT('%',#{questioner,jdbcType=VARCHAR}, '%'))
AND (A.ANSWERER LIKE CONCAT('%',#{answerer,jdbcType=VARCHAR}, '%') OR H.USERNAME LIKE CONCAT('%', #{answerer,jdbcType=VARCHAR}, '%'))
AND DATE_FORMAT(A.QUESTIONDATE, '%Y/%m/%d') >= #{questionDateFrom,jdbcType=DATE}
AND DATE_FORMAT(A.QUESTIONDATE, '%Y/%m/%d') <= #{questionDateTo,jdbcType=DATE}
AND DATE_FORMAT(A.EXPDATE, '%Y/%m/%d') >= #{expDateFrom,jdbcType=DATE}
AND DATE_FORMAT(A.EXPDATE, '%Y/%m/%d') <= #{expDateTo,jdbcType=DATE}
AND DATE_FORMAT(A.ANSWERDATE, '%Y/%m/%d') >= #{answerDateFrom,jdbcType=DATE}
AND DATE_FORMAT(A.ANSWERDATE, '%Y/%m/%d') <= #{answerDateTo,jdbcType=DATE}
AND A.COMPANYFLG = '0'
AND A.COMPANYFLG = '1'
AND ( A.COMPANYFLG = '0' OR A.COMPANYFLG = '1')
AND A.COMPANYFLG = '1'

mapper.update:

UPDATE tbl_QA
SET QAQSTATE = '2',
 UPDATERID = #{user.userId,jdbcType=VARCHAR}
WHERE
	PROJECTID = #{projectId,jdbcType=VARCHAR}
AND QANO = #{relationQaNo,jdbcType=VARCHAR}
AND UPDATETIME = #{updateTime,jdbcType=TIMESTAMP}

mapper.select:

SELECT
	UPDATETIME
FROM
	tbl_qa
WHERE
	PROJECTID = #{projectId,jdbcType=VARCHAR}
AND QANO = #{qaNo,jdbcType=VARCHAR}
AND UPDATETIME = #{updateTime,jdbcType=TIMESTAMP}

SELECT PROJECTID , QANO, GROUPID, MODULEID, COMPANYFLG, QAQSTATE, QAASTATE, STAGE, QUESTIONER, DATE_FORMAT(QUESTIONDATE, '%Y/%m/%d') AS QUESTIONDATE, DATE_FORMAT(EXPDATE, '%Y/%m/%d') AS EXPDATE, ANSWERER, DATE_FORMAT(ANSWERDATE, '%Y/%m/%d') AS ANSWERDATE, QATitle, QATitle_CN, QUESTIONINFO, QUESTIONINFO_CN, QATTACHEDINFO, ANSWERINFO, ANSWERINFO_CN, AATTACHEDINFO, QUESTIONDIV, CAUSEDIV, RELATIONQANO, REMARK, REMARK_CN, UPDATERID, UPDATETIME FROM
tbl_QA
tbl_QAHistory
WHERE PROJECTID=#{projectId,jdbcType=VARCHAR} AND QANO=#{qaNo,jdbcType=VARCHAR}
AND SERIALNUM=#{serialNum,jdbcType=VARCHAR}

